package org.mobidics.data;

import org.apache.commons.net.ftp.FTP;
import org.apache.commons.net.ftp.FTPClient;
import org.mobidics.api.viewmodel.MethodViewModel;

import javax.imageio.ImageIO;
import javax.xml.bind.DatatypeConverter;
import java.awt.image.BufferedImage;
import java.io.*;
import java.util.List;

/**
 * Created by Long on 10.06.2017.
 * E-Mail: longbui1992@gmail.com
 */
public class MobidicsFTPDAO
{
    private final String FTP_SERVER = "localhost";
    private final int FTP_PORT = 21;
    private final String FTP_USERNAME = "admin";
    private final String FTP_PASSWORD = "lb92bl29";
    private final String FTP_FILESPATH = "mobidics/files/";

    public void uploadFileToServer(List<String> imageDataURIs)
    {
        FTPClient ftpClient = new FTPClient();
        try
        {
            ftpClient.connect(FTP_SERVER, FTP_PORT);
            ftpClient.login(FTP_USERNAME, FTP_PASSWORD);
            ftpClient.enterLocalPassiveMode();
            ftpClient.setFileType(FTP.BINARY_FILE_TYPE);

            String firstRemoteFile = FTP_FILESPATH + "testImage.jpg";
            String str = "data:image/jpeg;base64,";
            byte[] imagedata = DatatypeConverter.parseBase64Binary(str.substring(str.indexOf(",") + 1));
            ImageIO.setUseCache(false);
            BufferedImage bufferedImage = ImageIO.read(new ByteArrayInputStream(imagedata));
            OutputStream outputStream = ftpClient.storeFileStream(firstRemoteFile);
            ImageIO.write(bufferedImage, "jpg", outputStream);
            outputStream.close();
            boolean completed = ftpClient.completePendingCommand();
            if (completed)
            {
                System.out.println("File Upload complete!");
            }
        }
        catch (IOException ex)
        {
            System.out.println("Error: " + ex.getMessage());
            ex.printStackTrace();
        }
        finally
        {
            try
            {
                if (ftpClient.isConnected())
                {
                    ftpClient.logout();
                    ftpClient.disconnect();
                }
            }
            catch (IOException ex)
            {
                ex.printStackTrace();
            }
        }
    }
}
